package week1.day2;

public class FindEvenNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int maxRange = 10;
		// print even numbers from 1 to 10
		
		for (int i = 1; i<=maxRange; i++) {
			if (i%2==0) {
				System.out.println(i);
			}
		}
	
		}
	}

